-- lifeline_template >>
DROP TABLE IF EXISTS lifeline_template;
CREATE TABLE IF NOT EXISTS lifeline_template (
    id integer PRIMARY KEY AUTOINCREMENT,
    created_at timestamp CONSTRAINT df_lifeline_template_created_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    updated_at timestamp CONSTRAINT df_lifeline_template_updated_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    name text NOT NULL
);
-- << lifeline_template

-- question_template >>
DROP TABLE IF EXISTS question_template;
CREATE TABLE IF NOT EXISTS question_template (
    id integer PRIMARY KEY AUTOINCREMENT,
    created_at timestamp CONSTRAINT df_question_template_created_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    updated_at timestamp CONSTRAINT df_question_template_updated_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    contents text NOT NULL,
    answer_1 text NOT NULL,
    answer_2 text NOT NULL,
    answer_3 text NOT NULL,
    answer_4 text NOT NULL,
    correct_answer integer NOT NULL,
    CONSTRAINT c_question_template_correct_answer CHECK (correct_answer BETWEEN 1 AND 4)
);
-- << question_template

-- quiz >>
DROP TABLE IF EXISTS quiz;
CREATE TABLE IF NOT EXISTS quiz (
    id integer PRIMARY KEY AUTOINCREMENT,
    created_at timestamp CONSTRAINT df_quiz_created_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    updated_at timestamp CONSTRAINT df_quiz_updated_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    owner text NOT NULL
);
-- << quiz

-- quiz_question >>
DROP TABLE IF EXISTS question;
DROP TABLE IF EXISTS quiz_question;
CREATE TABLE IF NOT EXISTS quiz_question (
    id integer PRIMARY KEY AUTOINCREMENT,
    created_at timestamp CONSTRAINT df_quiz_question_created_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    updated_at timestamp CONSTRAINT df_quiz_question_updated_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    question_template_id integer NOT NULL,
    quiz_id integer NOT NULL,
    answer integer NULL,
    time_remaining integer CONSTRAINT df_quiz_question_time_remaining DEFAULT 15 NOT NULL,
    answered boolean CONSTRAINT df_quiz_question_answered DEFAULT false NOT NULL,
    CONSTRAINT fk_quiz_question_question_template_id FOREIGN KEY (question_template_id) REFERENCES question_template (id),
    CONSTRAINT fk_quiz_question_quiz_id FOREIGN KEY (quiz_id) REFERENCES quiz (id),
    CONSTRAINT u_quiz_question_question_template_id_quiz_id UNIQUE(question_template_id, quiz_id),
    CONSTRAINT c_quiz_question_answer CHECK (answer IS NULL OR answer BETWEEN 1 AND 4)
);
-- << quiz_question

-- quiz_lifeline >>
DROP TABLE IF EXISTS lifeline;
DROP TABLE IF EXISTS quiz_lifeline;
CREATE TABLE IF NOT EXISTS quiz_lifeline (
    id integer PRIMARY KEY AUTOINCREMENT,
    created_at timestamp CONSTRAINT df_quiz_lifeline_created_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    updated_at timestamp CONSTRAINT df_quiz_lifeline_updated_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    lifeline_template_id integer NOT NULL,
    quiz_id integer NOT NULL,
    used boolean CONSTRAINT df_quiz_lifeline_used DEFAULT false NOT NULL,
    CONSTRAINT fk_quiz_lifeline_lifeline_template_id FOREIGN KEY (lifeline_template_id) REFERENCES lifeline_template (id),
    CONSTRAINT fk_quiz_lifeline_quiz_id FOREIGN KEY (quiz_id) REFERENCES quiz (id),
    CONSTRAINT u_quiz_lifeline_lifeline_template_id_quiz_id UNIQUE(lifeline_template_id, quiz_id)
);
-- << quiz_lifeline

-- quiz_statistics >>
DROP TABLE IF EXISTS statistics;
CREATE TABLE IF NOT EXISTS statistics (
    id integer PRIMARY KEY AUTOINCREMENT,
    created_at timestamp CONSTRAINT df_statistics_created_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    updated_at timestamp CONSTRAINT df_statistics_updated_at DEFAULT CURRENT_TIMESTAMP NOT NULL,
    quiz_id integer REFERENCES quiz (id) NOT NULL,
    data text NULL,
    CONSTRAINT u_statistics_quiz_id UNIQUE(quiz_id)
);
